package com.relesi.impedanciamongo.config;import com.relesi.impedanciamongo.domain.User;import com.relesi.impedanciamongo.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.context.annotation.Configuration;import java.util.Arrays;@Configurationpublic class Intantiation implements CommandLineRunner {    @Autowired    private UserRepository userRepository;    @Override    public void run(String... args) throws Exception {        userRepository.deleteAll();        User renato = new User(null, "Renato Lessa", "renato@gmail.com");        User zezinho = new User(null, "Zezinho Lessa", "zezinho@gmail.com");        User maria = new User(null, "maria Lessa", "maria@gmail.com");        userRepository.saveAll(Arrays.asList(renato, zezinho, maria));    }}