package com.relesi.impedanciamongo.resources;import com.relesi.impedanciamongo.domain.User;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;import java.util.Arrays;import java.util.List;@RestController@RequestMapping(value = "/users")public class UserResources {    @RequestMapping(method= RequestMethod.GET)    public ResponseEntity<List<User>> findAll() {        User renato = new User("1", "Renato Lessa", "renatolessa.silva@gmail.com");        User nicolas = new User("2", "Nicolas Lessa", "nicolas.silva@gmail.com");        List<User> list = new ArrayList<>();        list.addAll(Arrays.asList(renato, nicolas));        return ResponseEntity.ok().body(list);    }}