package com.relesi.impedanciamongo.services;import com.relesi.impedanciamongo.domain.User;import com.relesi.impedanciamongo.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class UserService {    @Autowired    private UserRepository repo;    public List<User> findAll() {        return repo.findAll();    }}